require 'base64'
require 'utilityapi'

FactoryGirl.define do
  initialize_with { new(attributes) }
  skip_create

  # factory to create AccountOptions class, supports specifying auth_type
  factory :account_options, class: UtilityApi::Models::AccountOptions do
    utility 'DEMO'
    auth_type 'owner'
    real_name { auth_type == 'owner' ? 'Test Test' : nil }
    third_party_file do
      if auth_type == '3rdparty'
        # this is a base64-encoded string, representing an image
        ''
      else
        nil
      end
    end
    utility_username 'test'
    utility_password 'test'
  end
end
